	//保存理赔清单数据到临时表中
	private boolean saveToTempTable(List planting31SettleList, String registNo, HttpServletRequest request)throws Exception{
		boolean saveResult = false;
		if(null!=planting31SettleList && planting31SettleList.size()>0){
			DBManager dbManager = new DBManager();
			String timeStamp = generateTimeStamp();
			try {
				dbManager.open("NXDADataSource");
				dbManager.beginTransaction();
				Planting31SettleListDto planting31SettleListDto = null;
				BLPlanting31SettleListTempAction blPlanting31SettleListTempAction = new BLPlanting31SettleListTempAction();
				for(int index=0;index<planting31SettleList.size();index++){
					planting31SettleListDto = (Planting31SettleListDto)planting31SettleList.get(index);
					Planting31SettleListTempDto planting31SettleListTempDto = planting31SettleListDto.evaluate(registNo, index, timeStamp);
					blPlanting31SettleListTempAction.insert(dbManager, planting31SettleListTempDto);				
				}
				dbManager.commitTransaction();
				saveResult = true;
				request.setAttribute("timeStamp", timeStamp);
			}catch (Exception e) {
				dbManager.rollbackTransaction();
				// TODO Auto-generated catch block
				e.printStackTrace();
			}finally{
				dbManager.close();
			}
		}
		return saveResult;
	}
	//根据报案号删除理赔清单临时表中的对应数据。
	private void deletePreviousTempData(String registNo, String timeStamp)throws Exception{
		String sqlCondition = " registCode = '"+registNo+"' and stringTimeStamp = '"+timeStamp+"' ";
		BLPlanting31SettleListTempFacade blPlanting31SettleListTempFacade = new BLPlanting31SettleListTempFacade();
		blPlanting31SettleListTempFacade.deleteByConditions(sqlCondition);
	}
	private String generateTimeStamp(){
		String timeStamp = "";
		Date currentTime = new Date();
		timeStamp=new SimpleDateFormat("yyMMddHHmmss").format(currentTime);
		return timeStamp;
	}	
